		if( charactersMovements[i].angleModifier > 3 ) {
			characterBodyAnimations.start('run');
		} else {
			characterBodyAnimations.start('walk');
		}
		charactersAnimations[i] = characterBodyAnimations;

		charactersVisible[i] = true;

